/*
The bit-broker connector database intialisation script. Contains all tables, keys, indexes,
constrains, enums, users, roles, grants, etc.
*/

\connect bbk_connector_data

-- country table

CREATE TABLE country
(
    id VARCHAR (32) PRIMARY KEY,
    properties JSONB NOT NULL,
    created_at TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP
);



-- annual population timeseries table

CREATE TABLE annual_pop_ts
(
    entity_id   VARCHAR (32) NOT NULL,
    ts_from     integer NOT NULL, 
    ts_value    integer
);

CREATE INDEX idx_entity_id ON annual_pop_ts (entity_id);
CREATE INDEX idx_ts_from ON annual_pop_ts (ts_from);



-- heritagesites table

CREATE TABLE heritagesites
(
    id integer PRIMARY KEY,
    properties JSONB NOT NULL,
    created_at TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,
    updated_at TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP
);



-- grant permissions - TODO allocate more specific grants based on each user

GRANT SELECT ON ALL TABLES IN SCHEMA public TO bbk_connector_reader;
GRANT SELECT, INSERT, UPDATE, DELETE ON ALL TABLES IN SCHEMA public TO bbk_connector_writer;
GRANT USAGE, SELECT ON ALL SEQUENCES IN SCHEMA public TO bbk_connector_writer;
